
CREATE TABLE IF NOT EXISTS `products` (
    `product_id` varchar(20) NOT NULL COMMENT '商品ID',
    `product_name` varchar(100) NOT NULL COMMENT '商品名称',
    `description` text COMMENT '商品详细描述',
    `specifications` text COMMENT '商品规格参数',
    `usage_info` text COMMENT '使用说明信息',
    `brand` varchar(50) DEFAULT NULL COMMENT '品牌名称',
    `price` decimal(10,2) NOT NULL COMMENT '商品价格',
    `stock_quantity` int(11) NOT NULL DEFAULT '0' COMMENT '库存数量',
    `category_id` varchar(20) DEFAULT NULL COMMENT '分类ID',
    `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态：1-上架，0-下架',
    PRIMARY KEY (`product_id`),
    KEY `idx_brand` (`brand`),
    KEY `idx_category` (`category_id`),
    KEY `idx_price` (`price`),
    KEY `idx_status` (`status`)
    ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='商品表';



